using System;
using System.Data;
using System.Drawing;
using System.Text;
using System.Windows.Forms;
using PC.Manager;
using System.IO;

namespace mainPC.Windows.mainPC.상품관리 {        
    
    public partial class Product_add_Pop : Form {
        private DataRow m_prod_row = null;
        public Product_add_Pop() {
            InitializeComponent();
        }

        private bool ValidateTest() {
            return true;
        }
        private void Product_add_Pop_Load(object sender, EventArgs e) {
            if (m_prod_row == null) {
                if (m_prod_row == null) {
                    tbox_name.Text = "";
                    tbox_price.Text = "";
                    tbox_memo.Text = "";
                    string _stf_picture = "";
                    byte[] _byte = HexStringToByteArray(_stf_picture);
                    pbox_picture.Image = new Bitmap(new MemoryStream(_byte));
                    pbox_picture.Tag = null;
                }
            }
        }

        private byte[] HexStringToByteArray(string hex) {
            int NumberChars = hex.Length;
            byte[] bytes = new byte[NumberChars / 2];
            if (NumberChars % 2 == 0) {
                for (int i = 0; i < NumberChars; i += 2) {
                    bytes[i / 2] = Convert.ToByte(hex.Substring(i, 2), 16);
                }
            }
            return bytes;
        }


        public void btn_add_Click(object sender, EventArgs e) {
            if (ValidateTest()) {
                string _p_name = tbox_name.Text;
                DataRow _row = App.Self().DBManager.ReadProduct(_p_name);
                if (_row != null) {
                    MessageBox.Show(String.Format("{0}은 사용중인 상품명입니다.", _p_name));
                    tbox_name.Focus(); tbox_name.SelectAll();
                    return;
                }
                string _p_category = tbox_category.Text;
                string _p_price = tbox_price.Text;
                string _p_memo = tbox_memo.Text;                
                string _p_picture = "";
                if (pbox_picture.Tag != null) {
                    _p_picture = pbox_picture.Tag.ToString();
                }
                int _result = App.Self().DBManager.AddProduct(_p_name, _p_price, _p_memo, _p_picture,_p_category );
                if (_result > 0) {
                    MessageBox.Show("추가성공");
                    string _stf_picture = "";
                    byte[] _byte = HexStringToByteArray(_stf_picture);
                    pbox_picture.Image = new Bitmap(new MemoryStream(_byte));
                    pbox_picture.Tag = null;
                    tbox_category.SelectedIndex = -1;
                    tbox_memo.Clear();
                    tbox_name.Clear();
                    tbox_price.Clear();
                }
                else {
                    MessageBox.Show("추가실패");
                }
            }
        }

        private void btn_close_Click(object sender, EventArgs e) {
            DialogResult = DialogResult.Cancel;
        }

        private void btn_file_Click(object sender, EventArgs e) {
            OpenFileDialog ofd = new OpenFileDialog();
            string _image_file = string.Empty;
            ofd.Filter = "PNG|*.png|JPG|*.jpg|BMP|*.bmp|all files|*.*";
            ofd.InitialDirectory = Application.StartupPath;
            if (ofd.ShowDialog() == DialogResult.OK) {
                _image_file = ofd.FileName;
                pbox_picture.Image = Bitmap.FromFile(_image_file);

                FileStream fs = new FileStream(ofd.FileName, FileMode.Open, FileAccess.Read); ;
                byte[] bImage = new byte[fs.Length];
                fs.Read(bImage, 0, (int)fs.Length);

                StringBuilder hex = new StringBuilder(bImage.Length * 2);
                foreach (byte b in bImage) {
                    hex.AppendFormat("{0:X2}", b);
                }
                string _hex_image = hex.ToString();
                pbox_picture.Tag = _hex_image;
            }
        }
    }
}
